import style from "./AddForm.module.css";
import React, { useState } from "react";
import api from "../../api";
import { useHistory } from 'react-router-dom';
import { BrowserRouter } from 'react-router-dom';

const AddForm = (props) => {
  //    const [base64String, setBase64String] = useState("");
  const history = useHistory();

  const [formData, setFormData] = useState({
    title: "",
    image: "",
    desc: "",
  });

  function handleFileUpload(event) {
    var file = event.target.files[0];
    var reader = new FileReader();
    reader.onloadend = function () {
      setFormData((prevstate) => ({ ...prevstate, image: reader.result }));
    };
    reader.readAsDataURL(file);
  }

  const handleSubmit = async (event) => {
    event.preventDefault();
    const response = await api.post("/books", formData);
    if(response.statusText === "OK" ){
      props.onSubmit()
      history.push('/new-route');

    }
    else{
      console.log(response);
    }
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  return (
    <BrowserRouter>
    <form className={style.card} onSubmit={handleSubmit}>
      <label className={style["form-card"]}>
        Title
        <input
          className={style.input}
          type="text"
          name="title"
          value={formData.title}
          onChange={handleChange}
        />
      </label>
      <label className={style["form-card"]}>
        description
        <input
          className={style.input}
          type="text"
          name="desc"
          value={formData.desc}
          onChange={handleChange}
        />
      </label>
      <label className={style["form-card"]}>
        Image
        {/* <input className={style.input} type="text" name="image" value={formData.image} onChange={handleChange} /> */}
        <div className={style.imageInput}>
          <input type="file" onChange={handleFileUpload} />
          {formData.image && (
            <img
              className={style.preImage}
              src={formData.image}
              alt="Uploaded"
            />
          )}
        </div>
      </label>
      <button className={style["submit-btn"]} type="submit">
        Submit
      </button>
    </form>
    </BrowserRouter>
  );
};

export default AddForm;
