import style from "./SignupForm.module.css";
import React, { useState } from "react";
import api from "../../api";

const SignupForm = ( ) => {
  //    const [base64String, setBase64String] = useState("");

  const [formData, setFormData] = useState({
    user_name: "",
    img: "",
    password:'',
    email:''
  });

  function handleFileUpload(event) {
    var file = event.target.files[0];
    var reader = new FileReader();
    reader.onloadend = function () {
      setFormData((prevstate) => ({ ...prevstate, img: reader.result }));
    };
    reader.readAsDataURL(file);
  }

  const handleSubmit = async (event) => {
    event.preventDefault();
    await api.post("/users", formData);
    // response.statusText === "OK" ? props.onSubmit() : console.log(response);
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  return (
    <form className={style.card} onSubmit={handleSubmit}>
      <label className={style["form-card"]}>
        User Name

      </label>
      <input
          className={style.input}
          type="text"
          name="user_name"
          value={formData.user_name}
          onChange={handleChange}
        />
      <label className={style["form-card"]}>
        Email
        <input
          className={style.input}
          type="text"
          name="user_name"
          value={formData.email}
          onChange={handleChange}
        />
      </label>
      <label className={style["form-card"]}>
        Password
        <input
          className={style.input}
          type="text"
          name="user_name"
          value={formData.password}
          onChange={handleChange}
        />
      </label>
      <label className={style["form-card"]}>
        Image
         <div className={style.imageInput}>
          <input type="file" onChange={handleFileUpload} />
          {formData.img && (
            <img
              className={style.preImage}
              src={formData.img}
              alt="Uploaded"
            />
          )}
        </div>
      </label>
      <button className={style["submit-btn"]} type="submit">
        Submit
      </button>
    </form>
  );
};

export default SignupForm;
